export default function Textform(props) {
    return (
        <div>
            <h3>{props.heading}</h3>
            <div className="mb-3">
                <textarea className="form-control" id="myBox" rows="10"></textarea>
            </div>
            <button className="btn btn-primary">Convert to uppercase</button>
        </div>
    )
}
