import React from 'react'

export default function Textform(props) {
    return (
        <div>
            <h1>{props.heading}</h1>
            <div className="form-group">
                <label for="myBox">Example input</label>
                <textarea className="form-control" id="myBox" rows="10"></textarea>
            </div>
        </div>
    )
}
