// import { logDOM } from '@testing-library/react';
import React, { useState } from 'react'

export default function Textform(props) {

    const handleUpClick = () => {
        setText(text.toUpperCase())
    }

    const handleOnChange = (event) => {
        setText(event.target.value)
    }

    const handleLowClick = () => {
        setText(text.toLowerCase())
    }

    const handleClear = (event) => {
        setText("")
    }
    const handleCopyText = ()=>{

        var myText = document.getElementById("myBox");
            myText.select();
            navigator.clipboard.writeText(myText.value);
    };


    const [text, setText] = useState("Enter text here");
    // use the function settext(" some text ") to change the state.


    const mystyle = {
        border: "2px solid black"
    }


    return (
        <>
            <div className='container'>
                <h3>{props.heading}</h3>
                <div className="mb-3">
                    <textarea className="form-control" style = {mystyle} onChange={handleOnChange} value={text}  id="myBox" rows="10"></textarea>
                </div>

                <button className={`btn btn-${props.mode} mx-2`} style = {mystyle} onClick={handleUpClick}>Convert to uppercase</button>
                <button className={`btn btn-${props.mode} mx-2`} style = {mystyle} onClick={handleLowClick}>Convert to lowercase</button>
                <button className={`btn btn-${props.mode} mx-2`} style = {mystyle} onClick={handleClear}>Clear Text</button>
                <button className={`btn btn-${props.mode} mx-2`} style = {mystyle} onClick={handleCopyText}>Copy Text</button>

            </div>
            <div className="container my-4">
                <h4>Your text summary</h4>
                <p>{text.split(" ").length} words, {text.length} characters</p>
                <p>{0.008 * text.split(" ").length} minutes read</p>
                <h4>Preview</h4>
                <p>{text}</p>
            </div>
        </>
    )
}
